/**
 * GTCommEvents 命名空間
 *
 * 包含三個枚舉類型定義遊戲通訊事件：
 * - `Game`: 遊戲操作相關事件。
 * - `BetOption`: 投注選項相關事件。
 * - `Comm`: 通用遊戲設置和命令相關事件。
 */
/**
 * Game 事件枚舉
 *
 * 定義與遊戲操作直接相關的事件。
 */
export declare enum Game {
    /** 觸發遊戲下注的事件 */
    SPIN = "game_spin_event",
    /** 接到onBeginGame回傳 */
    BEGIN_GAME = "beginGame",
    /** 該局遊戲結束 */
    ROUND_END = "round_end",
    /** 下注事件，通常在快速播放模式下使用 */
    TURBO = "game_turbo_event"
}
/**
 * BetOption 事件枚舉
 *
 * 定義與投注選項相關的事件。
 */
export declare enum BetOption {
    /** 設置注額列表數據 */
    SET_BET_LIST_DATA = "set_bet_list_data_event",
    /** 獲取當前總下注金額 */
    GET_TOTAL_BET = "update_bet_coin_event"
}
/**
 * Comm 事件枚舉
 *
 * 定義通用的遊戲相關設置和命令事件。
 */
export declare enum Comm {
    /** 事件用於退出遊戲 */
    EXIT_GAME = "exit_game",
    /** 設置會員餘額 */
    SET_CREDIT = "set_credit",
    /** 設置跑馬燈信息 */
    SET_MARQUEE = "set_maquee",
    /** 設置用戶ID */
    SET_USERID = "set_userID",
    SHOW_ALERT = "show_alert"
}
export type EventType = Game | BetOption | Comm;
